<script>
const Cookie = process.client ? require("js-cookie") : undefined;

export default {
  data() {
    return {
      form: "login",
      login:{      
        username: "",
        password: ""
      },
      register:{      
        Email: "",
        Mobile: "",
        Password: "",
        HotelManager:false
      }
    };
  },
  mounted(){
        if(Cookie.get('auth') !== undefined){
            this.$router.push("/Profile");
        }
    },
  methods: {
    changeform(form) {
      this.form = form;
    },
    async Login(name) {
      await this.$axios({
        method: "post",
        url: 'Users/Login',
        data: this.login
      })
        .then((data) => {      
         Cookie.set("auth", data.data.id); // saving token in cookie for server rendering   
         Cookie.set("hm", data.data.HotelManager);
         this.$toast.success("Successful Login");
          this.$router.push("/Profile");
        })
        .catch((e) => {
            console.log(e)
            this.$toast.error(e);
        });
    },
    async Register(name) {
      await this.$axios({
        method: "post",
        url: 'users',
        data: this.register
      })
        .then((data) => {      
         Cookie.set("auth", data.data.id); // saving token in cookie for server rendering   
         Cookie.set("hm", this.register.HotelManager);
         this.$toast.success("Successful Register");
          this.$router.push("/Profile");
        })
        .catch((e) => {
            console.log(e)
            this.$toast.error(e);
        });
    }  
  }
};
</script>